# Aggiungi i marker per ogni personaggio famoso
for person in famous_people:
    # Marker per la città di nascita
    folium.Marker(
        location=person["birth_coords"],
        popup=f"{person['name']} (Nascita: {person['birth_city']})",
        icon=folium.Icon(color='blue', icon='info-sign')
    ).add_to(italy_map)
    
    # Marker per la città di morte se disponibile
    if person["death_coords"]:
        folium.Marker(
            location=person["death_coords"],
            popup=f"{person['name']} (Morte: {person['death_city']})",
            icon=folium.Icon(color='red', icon='info-sign')
        ).add_to(italy_map)

